mapper.select:
SELECT bid_code ,user_buyer_id ,auction_goods_code ,user_seller_id ,bid_price ,bid_date FROM bid_tb
auction_goods_code=#{auctionGoodsCode} ORDER BY bid_price DESC
user_buyer_id = #{userBuyerId}

mapper.insert:

	SELECT CONCAT("bid",MAX(CAST(substring(bid_code,4)+1 AS
	DECIMAL)))
	FROM bid_tb

INSERT INTO bid_tb
(bid_code
, user_buyer_id
, auction_goods_code
,
user_seller_id
, bid_price
, bid_date)
VALUES
(#{bidCode}
, #{userBuyerId}
, #{auctionGoodsCode}
, #{userSellerId}
, #{bidPrice}
, NOW());
